mapper.update:
UPDATE board	SET writetitle =#{writetitle},
					writecontent =#{writecontent},
					writepeople =#{writepeople}
WHERE writenum = #{writenum}

mapper.select:
SELECT writenum, writetitle, writecontent, writepeople, writeday, view_cnt FROM (SELECT rownum rn, writenum, writetitle, writecontent, writepeople, writeday, view_cnt FROM (SELECT writenum, writetitle, writecontent, writepeople, writeday, view_cnt FROM board ORDER BY writenum desc)
AND rownum <= #{page} * #{perPageNum} )
WHERE rn > (#{page} -1) * #{perPageNum}
